import React, { useState } from 'react';
import './App.css';
import { Route, Link, BrowserRouter as Router, Switch, Redirect } from 'react-router-dom'
import Login from './Login';
import GuardedRoute from 'react-guarded-route'
import { Navbar, Nav } from 'react-bootstrap'
import Menu from './Menu'


const IsUserLogged = (checkIfSignedIn) => {
  let isUserSigned = sessionStorage.getItem('userLoggedIn')
  console.log(isUserSigned)
  return checkIfSignedIn ? isUserSigned !== null : isUserSigned === null
}

const Routes = () => {
  return (
    <Router>
      <Switch>
        <GuardedRoute exact path='/' component={MainTheme} validatorFunction={IsUserLogged(true)} redirectTo='/login'>
          <GuardedRoute path='/about' component={About} validatorFunction={IsUserLogged(true)} redirectTo='/login' />
        </GuardedRoute>

        <GuardedRoute path='/login' component={Login} validatorFunction={IsUserLogged(false)} redirectTo='/' />

        <Redirect to={'/notfound'} />
      </Switch>
    </Router>
  )
}

var MainTheme = () => {
  return (
    <div>
      <Menu />
      {this.props.children}
    </div>
  )
}

function App() {
  return (
    <div>
      {Routes()}
    </div>
  )
}

function About() {
  return (
    <div>
      <h1>About</h1>
    </div>
  )
}

function Home() {
  return (
    <div>
      <h1>Home</h1>
      <h1>Home</h1>
      <h1>Home</h1>
      <h1>Home</h1>
      <h1>Home</h1>
      <h1>Home</h1>
      <h1>Home</h1>
    </div>
  )
}

export default App;
