import React, { useState } from 'react';
import './App.css';
import { Route, Link, BrowserRouter as Router, Switch } from 'react-router-dom'
import Login from './Login';
import GuardedRoute from 'react-guarded-route'
import { Navbar, Nav } from 'react-bootstrap'
import Menu from './Menu'


const IsUserLogged = (checkIfSignedIn) => {
  let isUserSigned = sessionStorage.getItem('userLoggedIn')
  return checkIfSignedIn ? isUserSigned !== null : isUserSigned === null
}

const UnauthenticatedRoutes = ({ match }) => {
  return (
    <Switch>
      <GuardedRoute path={match.url} validatorFunction={IsUserLogged(false)} component={Login} redirectTo='/' />
    </Switch>
  )
}

const AuthenticatedRoutes = ({ match }) => {
  return (
    <Switch>
      <GuardedRoute exact path={match.url} validatorFunction={IsUserLogged(true)} component={Home} redirectTo='/login' />
      <GuardedRoute path={match.url} about validatorFunction={IsUserLogged(true)} component={About} redirectTo='/login' />
    </Switch>
  )
}

function App() {
  return (
    <Router>
{IsUserLogged() ? AuthenticatedRoutes() : UnauthenticatedRoutes() }
    </Router>
  );
}

function About() {
  return (
    <div>
      <h1>About</h1>
    </div>
  )
}

function Home() {
  return (
    <div>
      <h1>Home</h1>
    </div>
  )
}

export default App;
