import React from 'react';

import axios from 'axios';


export default class Home extends React.Component {
  constructor(props) {
    super(props);

    this.replaceModalItem = this.replaceModalItem.bind(this);
    this.saveModalDetails = this.saveModalDetails.bind(this);
      this.state = {
        requiredItem: 0,
        brochure: []
      }
    // Axios.get('https://localhost:1337/sessions')
    //   .then(res => {
    //     console.log(res)
    //   })

    //  this.state.sessions.map(session => {
    //    console.log(Object.values(session)[0].Type)}
    //         this.state.sessions.map(session => {
    //           console.log(Object.values(session)[0].Type)
    //         })

    //console.log(this.state.sessions)
    // for (var i = 0; i < 2; i++) {
    //   this.state.brochure[i] = {
    //     sessionName: 'Lior Tembel',
    //     sessionType: 'RX',
    //     syncDirectory: '/home/Kappa',
    //     syslogIP: '0.0.0.0',
    //     snmpIP: '0.0.0.0'
    //   }
    // } 

  }



  // state = {
  //   sessions: []
  // }

  componentDidMount() {
    axios.get(`https://localhost:1337/sessions`)
      .then(res => {
        const sessions = res.data.Sessions
        var brochure = []
        this.setState({ sessions });
        var index = 0
        this.state.sessions.map(session => {
          console.log(index)
          var currentSession = Object.values(session)[0]
          brochure[index] = {
            sessionName: currentSession.Name,
            sessionType: currentSession.Type,
            syncDirectory: currentSession.SyncDirectory.Path,
            bandwidth: currentSession.Bandwidth.Rate,
            syslogIP: '0.0.0.0',
            snmpIP: '0.0.0.0'
          }
          ++index
        this.setState({ brochure });
        })
      })
  }

  replaceModalItem(index) {
    this.setState({
      requiredItem: index
    });
  }

  saveModalDetails(item) {
    const requiredItem = this.state.requiredItem;
    let tempbrochure = this.state.brochure;
    tempbrochure[requiredItem] = item;
    this.setState({ brochure: tempbrochure });
  }

  deleteItem(index) {
    let tempBrochure = this.state.brochure;
    tempBrochure.splice(index, 1);
    this.setState({ brochure: tempBrochure });
  }


  render() {
    const brochure = this.state.brochure.map((item, index) => {
      return (
        <tr key={index}>
          <td>{item.sessionName}</td>
          <td>{item.sessionType}</td>
          <td>{item.syncDirectory}</td>
          <td>
            <button className="btn btn-primary" data-toggle="modal" data-target="#exampleModal"
              onClick={() => this.replaceModalItem(index)}>edit</button> {" "}
            <button className="btn btn-danger" onClick={() => {
              //        console.log(index)
              this.deleteItem(index)
            }
            }>remove</button>
          </td>
        </tr>
      )
    });

    const requiredItem = this.state.requiredItem;
    let modalData = this.state.brochure[requiredItem];
    //console.log(modalData)
    return (
      <div>
        <div style={{ textAlign: "center" }}>
          <h1>Pillar of Salt Configuration</h1>
        </div>

        <table className="table table-striped">
          <thead>
            <tr>
              <th scope="col">Session Name</th>
              <th scope="col">Session Type</th>
              <th scope="col">Sync Directory</th>
              <th scope="col">Actions</th>
            </tr>
          </thead>
          <tbody>
            {brochure}
          </tbody>
        </table>
        { modalData ?
          <Modal
            sessionName={modalData.sessionName}
            sessionType={modalData.sessionType}
            syncDirectory={modalData.syncDirectory}
            bandwidth={modalData.bandwidth}
            snmpIP={modalData.snmpIP}
            syslogIP={modalData.syslogIP}
            saveModalDetails={this.saveModalDetails}
          />
          :
          null}

      </div>
    );
  }
}














